// Mendefinisikan dua variabel untuk perbandingan
let a = 10;
let b = 20;

// Menggunakan operator '==' untuk memeriksa apakah nilai 'a' dan 'b' sama
if (a == b) {
  console.log("a sama dengan b");
} else {
  console.log("a tidak sama dengan b"); // Output: a tidak sama dengan b
}

// Menggunakan operator '===' untuk memeriksa apakah nilai dan tipe data 'a' dan 'b' sama
if (a === b) {
  console.log("a sama persis dengan b");
} else {
  console.log("a tidak sama persis dengan b"); // Output: a tidak sama persis dengan b
}

// Menggunakan operator '!=' untuk memeriksa apakah nilai 'a' dan 'b' tidak sama
if (a != b) {
  console.log("a tidak sama dengan b"); // Output: a tidak sama dengan b
}

// Menggunakan operator '!==' untuk memeriksa apakah nilai atau tipe data 'a' dan 'b' tidak sama
if (a !== b) {
  console.log("a tidak sama persis dengan b"); // Output: a tidak sama persis dengan b
}

// Menggunakan operator '>' untuk memeriksa apakah 'a' lebih besar dari 'b'
if (a > b) {
  console.log("a lebih besar dari b");
} else {
  console.log("a tidak lebih besar dari b"); // Output: a tidak lebih besar dari b
}

// Menggunakan operator '<' untuk memeriksa apakah 'a' lebih kecil dari 'b'
if (a < b) {
  console.log("a lebih kecil dari b"); // Output: a lebih kecil dari b
}

// Menggunakan operator '>=' untuk memeriksa apakah 'a' lebih besar atau sama dengan 'b'
if (a >= b) {
  console.log("a lebih besar atau sama dengan b");
} else {
  console.log("a tidak lebih besar atau sama dengan b"); // Output: a tidak lebih besar atau sama dengan b
}

// Menggunakan operator '<=' untuk memeriksa apakah 'a' lebih kecil atau sama dengan 'b'
if (a <= b) {
  console.log("a lebih kecil atau sama dengan b"); // Output: a lebih kecil atau sama dengan b
}

// Menggunakan operator ternary (kondisional) untuk menentukan apakah 'a' lebih besar dari 'b'
let hasil = a > b ? "a lebih besar dari b" : "a tidak lebih besar dari b";
console.log(hasil); // Output: a tidak lebih besar dari b

